func CalculateFloor( input: String ) -> Int {
	var floor = 0
	for character in input {
		if character == "(" {
			floor = floor + 1
		}
		else if character == ")" {
			floor = floor - 1
		}
	}
	return floor 
}

func FindFirstFloor( input: String, test: Int ) -> Int {
	var floor = 0
	var charCount = 0
	for character in input {
		if character == "(" {
			floor = floor + 1
		}
		else if character == ")" {
			floor = floor - 1
		}
		charCount = charCount + 1
		if floor == -1 {
			return charCount
		}
	}
	return -1
}

func TestCalculation( testInput: String, testValue: Int ) {
	if CalculateFloor( input: testInput ) == testValue {
		print( "WORKS!" )
	}
	else
	{
		print( "DOESN'T WORK!" )
	}
}

