func CalculateFloor( input: String ) -> Int {
	var floor = 0
	for character in input {
		if character == "(" {
			floor = floor + 1
		}
		else if character == ")" {
			floor = floor - 1
		}
	}
	return floor 
}

func TestCalculation( testInput: String, testValue: Int ) {
	if CalculateFloor( input: testInput ) == testValue {
		print( "WORKS!" )
	}
	else
	{
		print( "DOESN'T WORK!" )
	}
}

